                        setSelectValue("")

                  }
            // toCommitSelect(e)
            // if(reset === true) {

            //       setSelectValue("")
            // }


      }
      const resetSelect = () => {
            if(reset === true) {
                  return ""
            }
      }


      return (
            <>
            {console.log(reset)}
                  <Select

                  setValue={selectValue}
                  //value={ selectValue }
                  //setValue={ () => resetSelect()}
                  className="basic-single"
                  classNamePrefix="select"
                  isDisabled={isDisabled}
                  isLoading={isLoading}
                  isRtl={isRtl}
                  name="name"
                  options={options}
                  onChange={ (e) => onChangeOption(e.value) }
                  closeMenuOnSelect={true}
                  tabIndex={0}
                  role="option"
                  />
                  <div className='masha'
                  style={{
                  color: 'hsl(0, 0%, 40%)',
                  display: 'inline-block',
                  fontSize: 12,
                  fontStyle: 'italic',
                  marginTop: '1em',
                  }} >
                  </div>
            </>
      )
}

export default CreateSelect;
