      return (
            <>
            {console.log(reset)}
                  <Select
                  //setValue={ () => resetSelect()}
                  className="basic-single"
                  classNamePrefix="select"
                  isDisabled={isDisabled}
                  isLoading={isLoading}
                  isRtl={isRtl}
                  name="name"
                  options={options}
                  onChange={ (newValue) => onChangeOption(newValue) }
