import * as React from 'react';
import { NavLink } from 'react-router-dom';
import { CompactTable } from '@table-library/react-table-library/compact';
import { useTheme } from '@table-library/react-table-library/theme';
import { getTheme } from '@table-library/react-table-library/baseline';
import { useSort } from '@table-library/react-table-library/sort';

// import { DocumentationSee } from '../documentation';
// import { nodes } from '../data';


function Table(){

      const nodes = [
            {
                  firstName: 'Lars',
                  lastName: 'Domenico',
                  birthdate: '1/15/2021',
                  startDate: '10/18/2021',
                  street: '3 Eagan Court',
                  city: 'San Bernardino',
                  state: 'California',
                  zipCode: '92415',
                  department: 'Services'
              },
              {
                  firstName: 'King',
                  lastName: 'Cartledge',
                  birthdate: '5/11/2021',
                  startDate: '1/18/2021',
                  street: '5 Del Sol Point',
                  city: 'Richmond',
                  state: 'Virginia',
                  zipCode: '23289',
                  department: 'Sales'
              },
              {
                  firstName: 'Devy',
                  lastName: 'Crampton',
                  birthdate: '5/26/2021',
                  startDate: '3/5/2021',
                  street: '84 Sheridan Road',
                  city: 'Lubbock',
                  state: 'Texas',
                  zipCode: '79452',
                  department: 'Sales'
              },
      ]
      const dataTableLabels = [
            { text: 'First Name', value: 'firstName' },
            { text: 'Last Name', value: 'lastName' },
            { text: 'Date of Birth', value: 'birthdate' },
            { text: 'Start Date', value: 'startDate' },
            { text: 'Street', value: 'street' },
            { text: 'City', value: 'city' },
            { text: 'State', value: 'state' },
            { text: 'Zip Code', value: 'zipCode' },
            { text: 'Department', value: 'department' }
      ]

      const key = 'Sort';
      const data = { nodes };
      const theme = useTheme(getTheme());
      const sort = useSort(
            data,
      {
            onChange: onSortChange,
      },
      {
            sortFns: {
            TASK: (array) => array.sort((a, b) => a.name.localeCompare(b.name)),
            DEADLINE: (array) => array.sort((a, b) => a.deadline - b.deadline),
            TYPE: (array) => array.sort((a, b) => a.type.localeCompare(b.type)),
            COMPLETE: (array) => array.sort((a, b) => a.isComplete - b.isComplete),
            TASKS: (array) => array.sort((a, b) => (a.nodes || []).length - (b.nodes || []).length),
            },
      },);

      function onSortChange(action, state) {
            console.log(action, state);
      }

      const COLUMNS = [
            { label: 'Task', renderCell: (item) => item.name, sort: { sortKey: 'TASK' } },
            {
                  label: 'Deadline',
                  renderCell: (item) =>
                  item.deadline.toLocaleDateString('en-US', {
                        year: 'numeric',
                        month: '2-digit',
                        day: '2-digit',
                  }),
                  sort: { sortKey: 'DEADLINE' },
            },
            { label: 'Type', renderCell: (item) => item.type, sort: { sortKey: 'TYPE' } },
            {
                  label: 'Complete',
                  renderCell: (item) => item.isComplete.toString(),
                  sort: { sortKey: 'COMPLETE' },
            },
            { label: 'Tasks', renderCell: (item) => item.nodes?.length, sort: { sortKey: 'TASKS' } },
      ];

      return (
            <>
                  <CompactTable columns={COLUMNS} data={data} theme={theme} sort={sort} />

                  {/* <br /> */}
                  {/* <DocumentationSee anchor={'Features/' + key} /> */}
            </>
      );

}

export default Table;
