                  isDisabled={isDisabled}
                  isLoading={isLoading}
                  isRtl={isRtl}
                  name="name"
                  options={options}
                  onChange={ (e) => setValue(e.label) }
                  closeMenuOnSelect={true}
                  tabIndex={0}
                  role="option"
                  value={ reset === true ? () => resetValue() : value}
