import React, { useState, useEffect } from 'react';
import Select from 'react-select';


function CreateSelect({reset, options, toCommitSelect}) {

      const [ selectedOption, onChangeSelect ] = useState('')
      console.log(reset)
      useEffect(() => {
            if(reset === true) {
              onChangeSelect("")
            }
      }, [reset])

      const [isDisabled, setIsDisabled] = useState(false);
      const [isLoading, setIsLoading] = useState(false);
      const [isRtl, setIsRtl] = useState(false);

      toCommitSelect(selectedOption)

      return (
            <>
                  <Select
                  value={selectedOption }
                  className="basic-single"
                  classNamePrefix="select"
                  isDisabled={isDisabled}
                  isLoading={isLoading}
                  isRtl={isRtl}
                  name="name"
                  options={options}
                  // onChange={(e) => setSelect(e.label)}
                  onChange={ (e) => onChangeSelect(e.label) }
                  // reset === true ? () => onChangeSelect("") : 
                  closeMenuOnSelect={true}
                  tabIndex={0}
                  role="option"
                  />
                  <div
                  style={{
                  color: 'hsl(0, 0%, 40%)',
                  display: 'inline-block',
                  fontSize: 12,
                  fontStyle: 'italic',
                  marginTop: '1em',
                  }} >
                  </div>
            </>
      )
}

export default CreateSelect;
