import React, { useState, useEffect } from 'react';
import Select from 'react-select';


function CreateSelect({reset, selectReset, options}) {

      const [ selectedOption, onChangeSelect ] = useState('')




      // useEffect(() => {
      //       if( stateReset == true) {
      //         onChangeSelect('')

      //       }
      // })

      //toCommitSelect()

      const [isDisabled, setIsDisabled] = useState(false);
      const [isLoading, setIsLoading] = useState(false);
      const [isRtl, setIsRtl] = useState(false);


      return (
            <>
                  <Select
                  selected={selectedOption}
                        //reset == true ? "" : selectedOption }
                  className="basic-single"
                  classNamePrefix="select"
                  isDisabled={isDisabled}
                  isLoading={isLoading}
                  isRtl={isRtl}
                  name="name"
                  options={options}
                  // onChange={(e) => setSelect(e.label)}
                  onChange={ reset === true ? () => onChangeSelect("") : (e) => onChangeSelect(e.label) }
                  closeMenuOnSelect={true}
                  defaultInputValue=""
                  tabIndex={0}
                  role="option"
                  />
                  <div
                  style={{
                  color: 'hsl(0, 0%, 40%)',
                  display: 'inline-block',
                  fontSize: 12,
                  fontStyle: 'italic',
                  marginTop: '1em',
                  }} >
                  </div>
            </>
      )
}

export default CreateSelect;
